fun getReplacementsAsMap(lines: List<String>): Map<String, List<String>> {
    // read the replacements and put them in a Map, value is list of replacements for element
    return lines.map { line ->
        val (from, to) = LINE_PATTERN.find(line)!!.destructured
        Pair(from, to)
    }.groupBy({ it.first }, { it.second })
}